@app.route("/")
@cross_origin()
def index():
    # @todo add rapid api token condition
    q = request.args.get('q', '')

    if q == '':
        return 'Zəhmət olmasa, axtarış sözünü göndərin!', 400

    output = list(
        filter(lambda r: q.lower() in (r[0] + r[1]).lower(), db)
    )

    return jsonify(output)